import GUI.PublicationTree;import Projects.Projects;import Projects.Project;import javafx.event.ActionEvent;import javafx.scene.control.Button;import java.lang.reflect.Array;import java.util.Observable;import java.util.Observer;public class Controler implements Observer {    private Projects projects;    private PublicationTree publicationTree;    @Override    public void update(Observable o, Object arg) {        if (arg instanceof Object[]){            Object[] args = (Object[]) arg;            ActionEvent event = (ActionEvent) args[0];            if (event.getSource() instanceof Button){                Button source = (Button) event.getSource();                if (source.getId().equals("0001")){                    addProject(new Project((String) args[1]));                }            }        }    }    public Controler(Projects projects, PublicationTree publicationTree){        this.projects = projects;        this.publicationTree = publicationTree;        this.publicationTree.addObserver(this);    }    public void addProject(Project project){        projects.addProject(project);        publicationTree.addProject(project.getName());    }}