package Controller;import View.GUI.PublicationTree;import Projects.Projects;import Projects.Project;import javafx.stage.Stage;public class Controler{    private Projects projects;    private PublicationTree publicationTree;    public Controler(Projects projects, Stage stage){        this.projects = projects;        this.publicationTree = new PublicationTree(this, stage);    }    public PublicationTree getPublicationTree() {        return publicationTree;    }    public void addProject(String name){        Project project = new Project(name);        projects.addProject(project);        publicationTree.addProject(project.getName());    }}