package Controller;import Model.Model;import Model.Projects.Publications.Book;import Model.Projects.Publications.Publication;import View.GUI.PublicationTree;import Model.Projects.Project;import View.View;import javafx.stage.Stage;import java.io.File;import java.nio.file.Path;import java.nio.file.Paths;public class Controller {    Model model;    View view;    private PublicationTree publicationTree;    public Controller(Model model, Stage stage){        this.model = model;        this.view = new View(model, this, stage);        view.create();    }    public PublicationTree getPublicationTree() {        return publicationTree;    }    public void addProject(File file){        if (file != null){            Project project = new Project(                    file.getName(),                    Paths.get(file.getPath()));            model.addProject(project);        }    }    public void remProject(Project project){        model.remProject(project);    }    public void addPub(Project project, File file) throws IllegalArgumentException {        if (file != null){            Publication pub = new Book(                    file.getName(),                    Paths.get(file.getPath()),                    project);            model.addPub(project, pub);        }    }    public void remPub(Project project, Publication publication){        model.remPub(project,publication);    }}