package Controller;import Model.Model;import View.GUI.PublicationTree;import Model.Projects.Project;import View.View;import javafx.stage.Stage;public class Controller {    Model model;    View view;    private PublicationTree publicationTree;    public Controller(Model model, Stage stage){        this.model = model;        this.view = new View(model, this, stage);        view.create();    }    public PublicationTree getPublicationTree() {        return publicationTree;    }    public void addProject(String name){        Project project = new Project(name);        model.addProject(project);    }    public void remProject(Project project){        model.remProject(project);    }}