package GUI;import javafx.scene.control.*;import javafx.scene.layout.HBox;import javafx.scene.layout.Pane;import javafx.scene.layout.VBox;import javafx.stage.DirectoryChooser;import javafx.stage.Stage;import java.io.File;import java.util.Observable;public class PublicationTree extends Observable {    private Stage stage;    private Pane layout;    private TreeView publicationTree;    public PublicationTree(Stage stage){        this.stage = stage;        publicationTree = createTree();        layout = create();    }    public Pane get() {        return layout;    }    public void addProject(String name){        publicationTree.getRoot().getChildren().add(new TreeItem<>(name));    }    private HBox createMenu(){        Button addProject = new Button("Add");        Button remProject = new Button("Remove");        addProject.setId("0001");        addProject.setOnAction(e->{            Object[] res = new Object[]{e,chooseDir()};            setChanged();            notifyObservers(res);        });        return new HBox(addProject, remProject);    }    private TreeView<String> createTree(){        TreeItem<String> dummyRoot = new TreeItem<>();        TreeView<String> publicationTreeView = new TreeView<>(dummyRoot);        publicationTreeView.setShowRoot(false);        new PublicationTreeContextMenu(publicationTreeView).createContextMenu();        return publicationTreeView;    }    private String chooseDir(){        DirectoryChooser dirChooser = new DirectoryChooser();        File dir = dirChooser.showDialog(stage);        return dir.getName();    }    private Pane create(){        return new VBox(publicationTree, createMenu());    }}