package Model;import Model.Projects.Project;import Model.Projects.Publications.Publication;import Model.Tasks.ProcessTask;import Model.Tasks.ProcessTaskSettings;import java.nio.file.Path;import java.util.ArrayList;import java.util.List;import java.util.Observable;import static View.ViewElement.*;public class Model extends Observable {    List<Project> projects = new ArrayList<>();    List<ProcessTask> tasks = new ArrayList<>();    boolean stopTaskQueue = false;    ProcessTaskSettings settings = ProcessTaskSettings.getInstance();    public ProcessTaskSettings getSettings() {        return settings;    }    public void addProject(Project project){        projects.add(project);        setChanged();        notifyObservers(PUBLICATION_TREE);    }    public void remProject(Project project){        projects.remove(project);        setChanged();        notifyObservers(PUBLICATION_TREE);    }    public void addPub(Project project, Publication publication){        project.addPublication(publication);        setChanged();        notifyObservers(PUBLICATION_TREE);    }    public void remPub(Project project, Publication publication){        project.removePublication(publication);        setChanged();        notifyObservers(PUBLICATION_TREE);    }    public void setTargetPath(Project project, Path path){        project.setTargetPath(path);        setChanged();        notifyObservers(CENTER_PANE);    }    public void addTask(ProcessTask task){        tasks.add(task);        setChanged();        notifyObservers(TASK_LIST);    }    public void remTask(ProcessTask task){        tasks.remove(task);        setChanged();        notifyObservers(TASK_LIST);    }    public void blockTaskQueue(){        stopTaskQueue = true;    }    public void unblockTaskQueue(){        stopTaskQueue = false;    }    public boolean isTaskQueueStopped(){        return stopTaskQueue;    }    public void addDefinedTask(ProcessTask task, int ind) throws IllegalArgumentException {        settings.addDefinedTask(task, ind);        setChanged();        notifyObservers(DEFINED_TASK);    }    public int remDefinedTask(ProcessTask task){        int ind = settings.remDefinedTask(task);        setChanged();        notifyObservers(DEFINED_TASK);        return ind;    }    public void saveSettings(){        settings.writeToFile();    }    public void restoreSettings() {        settings.readFromFile();        setChanged();        notifyObservers(DEFINED_TASK);    }    public List<Project> getProjects() {        return projects;    }    public List<ProcessTask> getTasks() {        return tasks;    }}