package Model;import Model.Projects.Project;import Model.Projects.Publications.Publication;import Model.Tasks.ProcessTask;import java.nio.file.Path;import java.util.ArrayList;import java.util.List;import java.util.Observable;import static View.ViewElement.*;public class Model extends Observable {    List<Project> projects = new ArrayList<>();    List<ProcessTask> tasks = new ArrayList<>();    public void addProject(Project project){        projects.add(project);        setChanged();        notifyObservers(PUBLICATION_TREE);    }    public void remProject(Project project){        projects.remove(project);        setChanged();        notifyObservers(PUBLICATION_TREE);    }    public void addPub(Project project, Publication publication){        project.addPublication(publication);        setChanged();        notifyObservers(PUBLICATION_TREE);    }    public void remPub(Project project, Publication publication){        project.removePublication(publication);        setChanged();        notifyObservers(PUBLICATION_TREE);    }    public void setTargetPath(Project project, Path path){        project.setTargetPath(path);        setChanged();        notifyObservers(CENTER_PANE);    }    public List<Project> getProjects() {        return projects;    }    public List<ProcessTask> getTasks() {        return tasks;    }}