package Model.Projects.Publications;import Model.Projects.ProjectItem;import java.nio.file.Path;public abstract class Publication implements ProjectItem {    Path sourcePath;    Path targetPath;    State currentState;    String name;    public Publication(String name){        this.name = name;    }    public Path getSourcePath() {        return sourcePath;    }    public void setSourcePath(Path sourcePath) {        this.sourcePath = sourcePath;    }    public String getName() {        return name;    }    public Path getTargetPath() {        return targetPath;    }    public void setTargetPath(Path targetPath) {        this.targetPath = targetPath;    }    @Override    public String toString() {        return name;    }}