package Model.Tasks;import javafx.scene.control.TextArea;public abstract class ProcessTask {    TextArea consoleTextArea;    public ProcessTask(TextArea consoleTextArea){        this.consoleTextArea = consoleTextArea;    }    public abstract javafx.concurrent.Task<Void> task();    public void run(){        Thread th = new Thread(task());        th.setDaemon(true);        th.start();    }}