package Projects;import Publications.Publication;import java.util.ArrayList;import java.util.List;public class Project {    List<Publication> publicationList = new ArrayList<>();    String name;    public Project(String name){        this.name = name;    }    public String getName() {        return name;    }    public boolean addPublication(Publication pub){        return publicationList.add(pub);    }    public boolean removePublication(Publication pub){        return publicationList.remove(pub);    }}