package Publications;import java.nio.file.Path;public abstract class Publication {    Path sourcePath;    Path targetPath;    State currentState;    String name;    public Path getSourcePath() {        return sourcePath;    }    public void setSourcePath(Path sourcePath) {        this.sourcePath = sourcePath;    }    public Path getTargetPath() {        return targetPath;    }    public void setTargetPath(Path targetPath) {        this.targetPath = targetPath;    }}