package Tasks;import javafx.concurrent.Task;import javafx.scene.control.TextArea;public abstract class ProcessingTask {    TextArea consoleTextArea;    public ProcessingTask(TextArea consoleTextArea){        this.consoleTextArea = consoleTextArea;    }    public abstract Task<Void> task();    public void run(){        Thread th = new Thread(task());        th.setDaemon(true);        th.start();    }}