package View.GUI;import Controller.Controller;import Model.Projects.Project;import javafx.geometry.Pos;import javafx.scene.layout.AnchorPane;import javafx.scene.layout.BorderPane;import javafx.scene.layout.Pane;import javafx.scene.control.Label;import javafx.stage.Stage;import java.awt.*;import static javafx.geometry.Pos.CENTER;public class ProjectPane {    Controller controller;    Stage stage; Pane publicPane;    public ProjectPane(Controller controller, Stage stage){        this.controller = controller;        this.stage = stage;    }    public Pane create(){        Label noProjectLab = new Label("There is no selected project");        publicPane = new BorderPane(noProjectLab);        BorderPane.setAlignment(noProjectLab, CENTER);        return publicPane;    }    public Pane update(Project project){        Label sourcePathLab = new Label("Path to source directory: ");        Label sourcePathVal = new Label(project.getSourcePath().toString());        return null;    }}