package View.GUI;import Controller.Controller;import Model.Projects.Project;import javafx.geometry.HPos;import javafx.geometry.VPos;import javafx.scene.layout.*;import javafx.scene.control.Label;import javafx.stage.Stage;import java.util.Objects;public class ProjectPane {    Controller controller;    Stage stage; GridPane publicPane;    public ProjectPane(Controller controller, Stage stage){        this.controller = controller;        this.stage = stage;    }    public Pane create(){        Label noProjectLab = new Label("There is no selected project");        publicPane = new GridPane();        ColumnConstraints col = new ColumnConstraints();        RowConstraints row = new RowConstraints();        col.setPercentWidth(100);        row.setPercentHeight(100);        publicPane.getColumnConstraints().add(col);        publicPane.getRowConstraints().add(row);        GridPane.setConstraints(noProjectLab,0,0,1,1,HPos.CENTER,VPos.CENTER);        publicPane.getChildren().add(noProjectLab);        return publicPane;    }    public void update(Project project){        if (project != null){            Label sourcePathLab = new Label("Path to source directory: ");            Label sourcePathVal = new Label(project.getSourcePath().toString());            publicPane.getChildren().removeIf(Objects::nonNull);            publicPane.getColumnConstraints().removeIf(Objects::nonNull);            publicPane.getRowConstraints().removeIf(Objects::nonNull);            GridPane.setConstraints(sourcePathLab,0,0,1,1);            GridPane.setConstraints(sourcePathVal,1,0,1,1);            publicPane.getChildren().addAll(sourcePathLab, sourcePathVal);        } else {            Label noProjectLab = new Label("There is no selected project");            GridPane.setConstraints(noProjectLab,0,0,1,1);            publicPane.getChildren().removeIf(Objects::nonNull);            publicPane.getChildren().addAll(noProjectLab);        }    }}