package View.GUI;import Controller.Controller;import Model.Projects.Project;import javafx.geometry.Pos;import javafx.scene.Node;import javafx.scene.layout.AnchorPane;import javafx.scene.layout.BorderPane;import javafx.scene.layout.HBox;import javafx.scene.layout.Pane;import javafx.scene.control.Label;import javafx.stage.Stage;import java.awt.*;import java.util.Objects;import java.util.Optional;import static javafx.geometry.Pos.CENTER;public class ProjectPane {    Controller controller;    Stage stage; Pane publicPane;    public ProjectPane(Controller controller, Stage stage){        this.controller = controller;        this.stage = stage;    }    public Pane create(){        Label noProjectLab = new Label("There is no selected project");        publicPane = new HBox(noProjectLab);        return publicPane;    }    public void update(Project project){        if (project != null){            Label sourcePathLab = new Label("Path to source directory: ");            Label sourcePathVal = new Label(project.getSourcePath().toString());            publicPane.getChildren().removeIf(Objects::nonNull);            publicPane.getChildren().addAll(sourcePathLab, sourcePathVal);        } else {            Label noProjectLab = new Label("There is no selected project");            publicPane.getChildren().removeIf(Objects::nonNull);            publicPane.getChildren().addAll(noProjectLab);        }    }}