package View;import Controller.Controller;import Model.Model;import View.GUI.PublicationTree;import javafx.scene.Scene;import javafx.scene.control.Button;import javafx.scene.control.TextArea;import javafx.scene.layout.BorderPane;import javafx.stage.Stage;import java.util.Observable;import java.util.Observer;import Model.ViewElement;import static Model.ViewElement.*;public class View implements Observer {    Model model;    Controller controller;    Stage primaryStage;    PublicationTree pubTree;    public View(Model model, Controller controller, Stage stage){        this.model = model;        this.controller = controller;        primaryStage = stage;        model.addObserver(this);    }    @Override    public void update(Observable o, Object arg) {        ViewElement message = arg instanceof ViewElement ? (ViewElement) arg : null;        if (message == PUBLICATION_TREE){            pubTree.update(model.getProjects());        }    }    public void create(){        pubTree = new PublicationTree(controller, primaryStage);        pubTree.create(model.getProjects());        TextArea logView = new TextArea("abrakadabra\nabrakadabra");        logView.setEditable(false);        Button addText = new Button("Add text");        Button getText = new Button("Get text");        Button clearText = new Button ("Clear");        getText.setOnAction(e -> System.out.println(logView.getText()));        addText.setOnAction(e -> {            logView.setText(logView.getText() + "\nabrakadabra");            logView.selectPositionCaret(logView.getLength());            logView.deselect();        });        BorderPane root = new BorderPane(logView,                                         addText,                                         clearText,                                         getText,                                         pubTree.create(model.getProjects()));        Scene scene = new Scene(root, 300, 300);        primaryStage.setTitle("Moje okno");        primaryStage.setScene(scene);        primaryStage.show();    }}