package View;import Controller.Controller;import Model.Model;import View.GUI.ProjectPane;import View.GUI.PublicationTree;import javafx.geometry.Pos;import javafx.scene.Scene;import javafx.scene.control.Button;import javafx.scene.control.TextArea;import javafx.scene.layout.BorderPane;import javafx.scene.layout.Pane;import javafx.stage.Stage;import java.util.Observable;import java.util.Observer;import static View.ViewElement.*;import static javafx.geometry.Pos.CENTER;public class View implements Observer {    Model model;    Controller controller;    Stage primaryStage;    PublicationTree pubTree; ProjectPane proPane;    public View(Model model, Controller controller, Stage stage){        this.model = model;        this.controller = controller;        primaryStage = stage;        model.addObserver(this);    }    @Override    public void update(Observable o, Object arg) {        ViewElement message = arg instanceof ViewElement ? (ViewElement) arg : null;        if (message == PUBLICATION_TREE){            pubTree.update(model.getProjects());        }    }    public void update(ViewElement message) {    }    public void create(){        pubTree = new PublicationTree(controller, primaryStage);        proPane = new ProjectPane(controller, primaryStage);        Pane pubTreeView = pubTree.create(model.getProjects());        Pane proPaneView = proPane.create();        pubTreeView.setPrefWidth(300);        TextArea logView = new TextArea("abrakadabra\nabrakadabra");        logView.setEditable(false);        Button addText = new Button("Add text");        Button getText = new Button("Get text");        Button clearText = new Button ("Clear");        getText.setOnAction(e -> System.out.println(logView.getText()));        addText.setOnAction(e -> {            logView.setText(logView.getText() + "\nabrakadabra");            logView.selectPositionCaret(logView.getLength());            logView.deselect();        });        BorderPane root = new BorderPane(proPaneView,                                         clearText,                                         getText,                                         logView,                                         pubTreeView);        Scene scene = new Scene(root, 800, 600);        primaryStage.setTitle("Moje okno");        primaryStage.setScene(scene);        primaryStage.show();    }}